import React, { useState } from "react";

function ChatHeader() {
  const [name, setName] = useState("");

  const handleNameUpdate = (messageId) => {
    // update the name state based on the selected message id
    switch (messageId) {
      case 1:
        setName("John");
        break;
      case 2:
        setName("Jane");
        break;
      case 3:
        setName("Bob");
        break;
      default:
        setName("");
        break;
    }
  };

  return (
    <div className="chatGrid-Head">
      <div className="chatInfo">
        <h2>Name: {name}</h2>
        <div className="headerIcon">
          <i class="fa-solid fa-phone"></i>
          <i class="fa-solid fa-phone"></i>
          <i class="fa-solid fa-phone"></i>
        </div>
      </div>
      <ChatSidebar onMessageSelect={handleNameUpdate} />
    </div>
  );
}

export default ChatHeader;
